import { Container, Row, Col, Nav, Navbar, Button } from 'react-bootstrap';
import './App.css';
import { useState } from 'react';
import data from './data'
import Detail from './pages/Detail';
import { Routes, Route, Link, useNavigate, Outlet } from 'react-router-dom'

function App() {

  let [shoes, setShoes] = useState(data);
  let navigate = useNavigate();

  return (
    <div className="App">
      <Navbar bg="dark" variant="dark">
        <Container>
          <Navbar.Brand onClick={()=>{navigate('/')}}>Navbar</Navbar.Brand>
          <Nav className="me-auto">
            <Nav.Link onClick={()=>{navigate('/')}}>Home</Nav.Link>
            <Nav.Link onClick={()=>{navigate(-1)}}>Features</Nav.Link>
            <Nav.Link onClick={()=>{navigate('/detail')}}>Pricing</Nav.Link>
            <Button onClick={()=>{console.log(shoes);let newShoes = [...shoes].sort();console.log(newShoes);setShoes(newShoes);}}>정렬</Button>
          </Nav>
        </Container>
      </Navbar>

      <Routes>
        <Route path='/' element={
          <>
            <div className='main-bg'></div>
            <Container>
              <Row>
                {
                  shoes.map((prod, i) => {
                    return(<Product prod={prod} i={i}/>)
                  })
                }
              </Row>
            </Container>
          </>
        }/>
        <Route path='/detail/:id' element={<Detail prod={shoes}/>}/>

        <Route path='/event' element={<Event/>}>
          <Route path='one' element={<div>첫 주문시 양배추 즙 서비스</div>}/>
          <Route path='two' element={<div>생일기념 쿠폰받기</div>}/>
        </Route>
      </Routes>
    </div>
  );
}

function Product(props){
  return (
    <Col>{props.i}
      <img src={'https://codingapple1.github.io/shop/shoes'+(props.i+1)+'.jpg'} width='80%'/>
      <h4>{props.prod.title}</h4>
      <p>{props.prod.content}</p>
    </Col>
  );
}

function Event(){
  return (
    <div>
      <h4>오늘의 이벤트</h4>
      <Outlet></Outlet>
    </div>
  );
}

export default App;
