import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

function Detail(props){

  let {id} = useParams();
  let [text, setText] = useState(100)
  let [alert, setAlert] = useState(true)

  useEffect(()=>{
    setTimeout(()=>{ setAlert(false) },2000);
  }, [])

    return(
      <div className="container">
        {
          alert == true ? 
          <div className="alert alert-warning">2초 이내 구매 시 할인</div> : 
          null
        }
        <div className="row">
          {
            props.shoes.map((prod, i) => {
              if(prod.id == id){
                return(
                  <>
                    <div className="col-md-6">
                      <img src={'https://codingapple1.github.io/shop/shoes'+(prod.id*1+1)+'.jpg'} width="100%" />
                    </div>
                    <div className="col-md-6">
                      <input>{text}</input>
                      <h4 className="pt-5">{prod.title}</h4>
                      <p>{prod.content}</p>
                      <p>{prod.price}</p>
                      <button className="btn btn-danger">주문하기</button> 
                    </div>
                  </>
                )
              }
            })
          }
        </div>
      </div> 
    );
  }

  export default Detail
